package week1.day1;

public class LearningDatatype {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int regno=345678;
		boolean isacar=true;
		float speed=0.1345667467f;
		char logo='N';
		String name="Nexon";
		long phno=8883543316l;
		double capacity=123.1234567890123;
		System.out.println("regno is:"+" "+ +regno);
		System.out.println("Logo is:"+" "+logo);
		System.out.println("Name of the car is"+" "+name);
		System.out.println("Phone Number is"+phno);
		System.out.println("The speed of the car is"+" "+speed);
		System.out.println("Capacity"+" "+capacity);
		System.out.println("Is a car"+" "+isacar);
	}

}
